// ─────────────────────────────────────────────
// Utility Exports
// ─────────────────────────────────────────────

/** All chapters flat list for a given class level */
export const getChaptersForClass = (classLevel: number): CBSEChapter[] =>
  Object.values(chaptersByClass[classLevel] ?? {}).flat()

/** All subject names for a given class level */
export const getSubjectsForClass = (classLevel: number): string[] =>
  Object.keys(chaptersByClass[classLevel] ?? {})

/** All chapters for a specific class and subject */
export const getChaptersForSubject = (classLevel: number, subject: string): CBSEChapter[] =>
  chaptersByClass[classLevel]?.[subject] ?? []

/** All CBSE chapters across all classes (flat) */
export const allCBSEChapters: CBSEChapter[] =
  Object.values(chaptersByClass).flatMap(subjects => Object.values(subjects).flat())
